// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract EasyAToken is ERC20 {
    constructor() ERC20("Token", "EasyAToken") {}

    function buy() public payable {
        require(msg.value > 0, "You must send some XRP to get tokens");
        _mint(msg.sender, msg.value);
    }
}

contracts/StockExchange.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract StockExchange {
    struct Stock {
        address owner;
        bytes6 id; // 6 bytes string (e.g., stock symbol)
        uint256 price; // Price per stock
        uint256 quantity; // Number of stocks available
    }

    Stock[] public stocks;
    mapping(bytes6 => uint256) public stockIndex;
    mapping(address => mapping(bytes6 => uint256)) public userStocks;

    event NewStock(bytes6 indexed id, address owner, uint256 price, uint256 quantity);
    event StockSold(bytes6 indexed id, address buyer, uint256 quantity);

    constructor() {
        // Initialize with some example stocks (for demonstration purposes)
        createStock("AAPL", 150, 100);
        createStock("GOOGL", 2500, 50);
    }

    modifier stockExists(bytes6 _id) {
        require(stockIndex[_id] > 0, "Stock does not exist");
        _;
    }

    function createStock(bytes6 _id, uint256 _price, uint256 _quantity) public {
        require(_price > 0, "Invalid price");
        require(_quantity > 0, "Invalid quantity");
        require(stockIndex[_id] == 0, "Stock already exists");

        stocks.push(Stock(msg.sender, _id, _price, _quantity));
        stockIndex[_id] = stocks.length;

        emit NewStock(_id, msg.sender, _price, _quantity);
    }

    function buyStock(bytes6 _id, uint256 _quantity) public payable stockExists(_id) {
        require(_quantity > 0, "Invalid quantity");

        uint256 stockIdx = stockIndex[_id] - 1;
        Stock storage stock = stocks[stockIdx];

        require(msg.value == stock.price * _quantity, "Incorrect payment amount");
        require(stock.quantity >= _quantity, "Insufficient stock quantity");

        stock.quantity -= _quantity;
        userStocks[msg.sender][_id] += _quantity;
        payable(stock.owner).transfer(msg.value);

        emit StockSold(_id, msg.sender, _quantity);
    }

    function getStockDetails(bytes6 _id) public view stockExists(_id)
        returns (address owner, uint256 price, uint256 quantity)
    {
        uint256 stockIdx = stockIndex[_id] - 1;
        Stock storage stock = stocks[stockIdx];

        return (stock.owner, stock.price, stock.quantity);
    }

    function getUserPortfolioValue() public view returns (uint256) {
        uint256 totalValue;
        for (uint256 i = 0; i < stocks.length; i++) {
            Stock storage stock = stocks[i];
            totalValue += userStocks[msg.sender][stock.id] * stock.price;
        }
        return totalValue;
    }
}

contracts/Voting.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

struct Proposal {
    string title;
    uint endBlock;
    uint yeas;
    uint noes;
}

contract Voting {
    event ProposalCreated(uint id);

    ERC20 public votingToken;
    mapping(uint => Proposal) public proposals;
    uint public proposalCounter = 0;

    constructor(address tokenAddress) {
        votingToken = ERC20(tokenAddress);
    }

    function createProposal(string memory title, uint endBlock) public  {
        require(bytes(title).length > 0, "title is empty");
        require(endBlock > block.number, "endBlock is in the past");

        uint proposalId = proposalCounter;
        proposals[proposalId] = Proposal({
            title: title,
            endBlock : endBlock,
            yeas: 0,
            noes: 0
        });

        proposalCounter++;
        emit ProposalCreated(proposalId);
    }

    function voteYes(uint proposalId) public {
        Proposal storage proposal = proposals[proposalId];

        require(proposal.endBlock > block.number, "proposal already ended");
        proposal.yeas = proposal.yeas + votingToken.balanceOf(msg.sender);
    }

    function voteNo(uint proposalId) public {
        Proposal storage proposal = proposals[proposalId];

        require(proposal.endBlock < block.number, "proposal already ended");
        proposal.noes = proposal.noes + votingToken.balanceOf(msg.sender);
    }

    function passed(uint proposalId) public view returns(bool) {
        Proposal storage proposal = proposals[proposalId];
        require(proposal.endBlock <= block.number, "proposal not ended");
        return proposal.yeas > proposal.noes;
    }
}

